      padding: 50px 50px 50px 70px;
      max-width: 50%;
      list-style: none;
    }

    &::before {
      position: absolute;
      top: -100px;
      left: 0;
      z-index: -1;
      opacity: 0.25;
      font-family: $serif;
      font-size: 200px;
      font-weight: 700;
      font-style: italic;
      color: $gray;
      counter-increment: li;
      content: counter(li, decimal-leading-zero);
      display: none;

      @include breakpoint(md) {
        display: block;
      }
    }

    h4 {
      font-size: 20px;
      font-weight: bold;
    }
  }
}

